// Simple Format Tweet Node Code
console.log('Starting format tweet process');

var embedData = [];
if ($json.embedArray) {
  embedData = $json.embedArray;
} else if ($json.body) {
  if ($json.body.embedArray) {
    embedData = $json.body.embedArray;
  }
}

console.log('Found embeds:', embedData.length);

var titleEmbed = null;
var thumbnailEmbed = null;

var i = 0;
while (i < embedData.length) {
  if (embedData[i].title === 'Title') {
    titleEmbed = embedData[i];
  }
  if (embedData[i].title === 'Thumbnail') {
    thumbnailEmbed = embedData[i];
  }
  i = i + 1;
}

if (!titleEmbed) {
  throw new Error('No title found in embed data');
}

function getStoreName(url) {
  var parts = url.split('://');
  var domain = parts[1] || parts[0];
  var hostParts = domain.split('/');
  var hostname = hostParts[0];
  var cleanDomain = hostname.replace('www.', '');
  var storeParts = cleanDomain.split('.');
  var storeName = storeParts[0];
  return storeName.charAt(0).toUpperCase() + storeName.slice(1);
}

function getDomain(url) {
  var parts = url.split('://');
  if (parts.length === 2) {
    var protocol = parts[0];
    var rest = parts[1].split('/')[0];
    return protocol + '://' + rest;
  }
  return 'https://www.google.com';
}

var storeName = getStoreName(titleEmbed.url);
var productTitle = titleEmbed.description || 'Product';
var productUrl = titleEmbed.url;
var thumbnailUrl = null;
if (thumbnailEmbed) {
  thumbnailUrl = thumbnailEmbed.url;
}

