var tweetText = $json.tweetText || '';
var hasImage = $json.hasImage || false;
var mediaIds = [];

// If we have an image, get the media ID from the upload response
if (hasImage && $json.media_id_string) {
  mediaIds.push($json.media_id_string);
  console.log('Including media ID:', $json.media_id_string);
} else {
  console.log('No image - posting text only');
}

// Format for Twitter API v2
var tweetData = {
  text: tweetText,
  card_uri: ""  // Prevent link previews
};

// Only add media if we have media IDs
if (mediaIds.length > 0) {
  tweetData.media = {
